const http2 = require('http2');
const fs = require('fs');

const server = http2.createServer();
server.on('error', (err) => console.error(err));

server.on('stream', router);

server.listen(8443);

function router(stream, headers) {

  stream.respond({
    'content-type': 'text/plain; charset=utf-8',
    ':status': 200
  });

  const path = headers[':path'];

  if (path === '/a') {
    timeConsumingProcessSimulation(stream, 10, path);
  } else if (path === '/b') {
    timeConsumingProcessSimulation(stream, 1, path);
  } else if (path === '/c') {
    timeConsumingProcessSimulation(stream, 10, path);
  } else if (path === '/d') {
    timeConsumingProcessSimulation(stream, 1, path);
  } else if (path === '/e') {
    timeConsumingProcessSimulation(stream, 4, path);
  }

}

function timeConsumingProcessSimulation(stream, seconds, path) {

  function handler() {

    if (seconds === 0) {
      sendCompletedEvent(stream, 'That\'s all on ' + path, path);
      return;
    }

    let message = path + ' ';

    for (let index = 0; index < 1; index++) {
      message += (`${seconds} remaining\n`);
    }

    sendProgressEvent(stream, message);

    timeConsumingProcessSimulation(stream, seconds - 1, path);

  }

  setTimeout(handler, 1000);

}

function sendProgressEvent(stream, message) {

  stream.write(JSON.stringify({ name: 'progress', message }) + '<EOP>');

}

function sendCompletedEvent(stream, message, path) {
  console.log('completing', path)
  stream.end(JSON.stringify({ name: 'completed', message }) + '<EOP>');

}
```

